import React, { useRef, useEffect, useState } from 'react';
import { TextField, Button, Container, Paper, Typography, Box, useTheme, useMediaQuery, responsiveFontSizes, Grid } from '@mui/material';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import SendIcon from '@mui/icons-material/Send';
// console.log(process.env.VITE_OPENAI_API_KEY);

const API_KEY = process.env.REACT_APP_OPENAI_API_KEY;

const systemMessage = {
    "role": "system",
    "content": "Remember about Heewon Kim, an ambitious and detail-oriented software developer, can be reached via email at khw0285@gmail.com. His professional profile can be viewed on LinkedIn (https://www.linkedin.com/in/heewon-kim-hkim/) and his projects are showcased on GitHub (https://github.com/Keemeeone). A comprehensive portfolio is available at https://keemeeone.github.io/. In summary, Heewon is a highly motivated individual with excellent communication skills, dedicated to fostering innovation. Proficient in various tools and languages including HTML5, CSS, JavaScript, TypeScript, React, Node.js, and more, he excels in collaborative environments and has a proven track record of driving projects from concept to completion. Heewon's experiences include a role as a Back-end Developer at the Wisconsin State Cartographer's Office, where he implemented an intuitive map interface and transformed complex XML files into accessible formats, significantly improving data accessibility. He also developed a FastAPI endpoint and an accurate URL retrieval system. As the Co-Founder and Front-end Developer of the College Mate App, Heewon designed user-friendly interfaces, implemented interactive features using React and React Native, and optimized page load times by 40%. In addition, he co-founded the Google Developer Club, organizing workshops and fostering a collaborative and innovative community. In the role of Product Manager and Back-end Developer at With U, Heewon led the development of a community app dedicated to saving abandoned dogs. He ensured seamless back-end operations, forged strong partnerships, and created a user experience aligned with the app's mission. At the CheeseHacks Hackathon, Heewon served as the Lead Developer (Front-end), collaboratively creating an interactive web application that enhanced understanding of front-end and back-end interactions. Heewon holds a Bachelor of Computer Science from the University of Wisconsin - Madison, completing relevant courses in object-oriented programming, data structures, machine organization, operating systems, and more. He also attended Madison Area Technical College from September 2019 to December 2020."
};

function AIChat() {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
    const responsiveTheme = responsiveFontSizes(theme);
    const [messages, setMessages] = useState([
        {
            message: "Hello, Ask me anything about Heewon!",
            sentTime: "just now",
            sender: "AI"
        }
    ]);
    const [isTyping, setIsTyping] = useState(false);
    const [userInput, setUserInput] = useState('');

    const handleSend = async () => {
        const newMessage = {
            message: userInput,
            direction: 'outgoing',
            sender: "user"
        };

        const newMessages = [...messages, newMessage];
        setMessages(newMessages);

        setIsTyping(true);
        await messageChatGPT(newMessages);
        setUserInput('');
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSend();
        }
    };

    async function messageChatGPT(chatMessages) {

        let apiMessages = chatMessages.map((messageObject) => {
            let role = (messageObject.sender === "AI") ? "assistant" : "user";
            return { role: role, content: messageObject.message };
        });

        const apiRequestBody = {
            "model": "gpt-3.5-turbo",
            "messages": [
                systemMessage,
                ...apiMessages
            ]
        };

        await fetch("https://api.openai.com/v1/chat/completions",
            {
                method: "POST",
                headers: {
                    "Authorization": "Bearer " + API_KEY,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(apiRequestBody)
            }).then((data) => data.json())
            .then((data) => {
                setMessages([...chatMessages, {
                    message: data.choices[0].message.content,
                    sender: "AI"
                }]);
                setIsTyping(false);
            });
    }

    return (
        <div>
            <Container sx={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h2" mb={isSmallScreen ? 10 : 5} style={{ fontSize: isSmallScreen ? "1.5em" : "3em", textAlign: "center" }}>
                    AI
                </Typography>
                <Paper style={{ width: '80%', scrollSnapType:'y' }}>
                    {/* <Typography variant="h5" textAlign={'center'}>
                        TALK ABOUT HEEWON
                    </Typography> */}
                    <Paper elevation={0} style={{ padding: "20px", marginTop: "20px", height: '40vh', overflowY: 'scroll', backgroundColor: '#FFF', scrollSnapAlign:'end' }}>
                        <Box sx={{ height: '100%' }}>
                            {messages.map((message, i) => (
                                <Box key={i}>
                                    <Box
                                        sx={{
                                            display: 'flex',
                                            alignItems: 'center',
                                            flexDirection: message.sender === 'user' ? 'row-reverse' : 'row',
                                        }}
                                    >
                                        <Typography style={{ margin: '8px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                                            {message.sender === 'user' ? <PersonIcon sx={{ color: '#236a46' }} /> : <SmartToyIcon sx={{ color: '#236a46' }} />}
                                        </Typography>
                                        <Paper
                                            elevation={5}
                                            sx={{
                                                maxWidth: '70%',
                                                marginBottom: '8px',
                                                // marginLeft: message.sender !== 'user' ? '8px' : 0,
                                                // marginRight: message.sender === 'user' ? '8px' : 0,
                                            }}
                                        >
                                            <Typography style={{ margin: '8px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                                                {message.message}
                                            </Typography>
                                        </Paper>
                                    </Box>
                                </Box>
                            ))}
                        </Box>
                    </Paper>

                    <Box mb={1} sx={{ marginTop: 'auto', textAlign: 'center', }}>
                <Grid container spacing={2} display={'flex'} justifyContent="center" alignItems="center">
                    <Grid item xs={12} sm={10}>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            fullWidth
                            id="message"
                            label="Type message here"
                            name="message"
                            value={userInput}
                            onChange={(e) => setUserInput(e.target.value)}
                            onKeyPress={handleKeyPress}

                            sx={{
                                fontSize: { xs: '8px', sm: '14px' },
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={1}>
                        {/* <Button
                            variant="contained"
                            fullWidth
                            onClick={handleSend}
                            sx={{
                                // height:'vh',
                                fontSize: { xs: '8px', sm: '14px' },
                                backgroundColor:'transparent',
                                color: '#0a1811',
                            }}
                        >
                            Send
                        </Button> */}
                        <SendIcon onClick={handleSend} sx={{ color: '#236a46', cursor:'pointer' }}/>
                    </Grid>
                </Grid>
            </Box>
                    {isTyping && <Typography style={{ marginTop: "8px", color:'#FFF'}}>AI is typing...</Typography>}
                </Paper>
            </Container>
        </div>
    );
}

export default AIChat;
