import React, { useRef, useEffect } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import { Container, Typography, Box, Button } from "@mui/material";

const Single = React.forwardRef(({ project }, ref) => {
  const imageUrl = project.imageUrls[0];

  // Use the `useRef` hook to create a ref for the `motion.div`
  const motionDivRef = useRef();

  const { scrollYProgress } = useScroll({
    // Use the `ref` as the target for scroll tracking
    target: ref,
  });

  // Apply the animation to the `motion.div` using the `y` motion value
  const y = useTransform(scrollYProgress, [0, 1], [-300, 300]);

  useEffect(() => {
    // Log the `y` motion value for debugging
    console.log("y:", y.get());
  }, [y]);

  return (
    <Container>
      <Box style={{ width: "100%" }}>
        {/* Attach the ref to the `motion.div` */}
        <motion.div ref={motionDivRef} style={{ y }}>
          <img
            alt={project.title}
            src={imageUrl}
            style={{
              minHeight: "0",
              minWidth: "0",
              maxHeight: "50vh",
              maxWidth: "80%",
              border: "1px solid #65f9af",
              borderRadius: "10px",
            }}
          />
          <Typography
            color={"#FFF"}
            fontWeight={"bold"}
            textAlign={"center"}
            mb={2}
            sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
          >
            {project.title}
          </Typography>
          <Typography
            color={"#FFF"}
            fontWeight={"bold"}
            textAlign={"center"}
            variant="body2"
            sx={{ fontSize: { xs: "6px", sm: "14px" } }}
          >
            {project.role}
          </Typography>
          <Typography
            color={"#FFF"}
            variant="body2"
            textAlign={"center"}
            mb={2}
            sx={{ fontSize: { xs: "6px", sm: "14px" } }}
          >
            {project.description}
          </Typography>
          <Box sx={{ marginTop: "auto", textAlign: "center" }}>
            {project.demoLink ? (
              <Button
                href={project.demoLink}
                variant="contained"
                target="_blank"
                sx={{
                  fontSize: { xs: "8px", sm: "14px" },
                  backgroundColor: "#65f9af",
                  color: "#327C57",
                }}
              >
                Demo
              </Button>
            ) : (
              <Typography
                variant="body2"
                color={"#FFF"}
                textAlign={"center"}
                mb={2}
                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
              >
                Demo Not Available
              </Typography>
            )}
          </Box>
        </motion.div>
      </Box>
    </Container>
  );
});

export default Single;
