import React, { useEffect, useState } from "react";

const ScrollHandler = ({ components, children }) => {
  const [visibleComponent, setVisibleComponent] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      // 현재 스크롤 위치 계산
      const scrollPosition = window.scrollY || document.documentElement.scrollTop;

      // 각 컴포넌트의 offsetTop 계산
      const componentOffsets = components.map((component) => {
        const element = document.getElementById(component);
        return {
          id: component,
          offsetTop: element.offsetTop,
        };
      });

      // 현재 보여져야 할 컴포넌트 결정
      const currentComponent = componentOffsets.find(
        (offset) => scrollPosition >= offset.offsetTop
      );

      if (currentComponent) {
        setVisibleComponent(components.indexOf(currentComponent.id));
      }
    };

    // 스크롤 이벤트 리스너 등록
    window.addEventListener("scroll", handleScroll);

    // 컴포넌트 언마운트 시 이벤트 리스너 제거
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [components]);

  return <>{children[visibleComponent]}</>;
};

export default ScrollHandler;
