import { useEffect } from "react";
import { motion, useTransform, useMotionValue } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography, useTheme, useMediaQuery } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100, scrollPosition }) {
    const baseX = useMotionValue(0);
    const x = useTransform(baseX, (v) => `${wrap(-50, 50, v)}%`);

    useEffect(() => {
        const updateX = () => {
            baseX.set((scrollPosition / 1000) * baseVelocity);
        };

        updateX();

        window.addEventListener("scroll", updateX);

        return () => {
            window.removeEventListener("scroll", updateX);
        };
    }, [scrollPosition, baseVelocity, baseX]);

    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', overflowX: 'hidden' }}>
            <motion.div style={{ x: x, width: '100%' }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h1" style={{ fontSize: isSmallScreen ? "3em" : "5.5rem", textAlign: "center" }}>
                    {children}
                </Typography>
            </motion.div>
        </div>
    );
}

export default function ProSkill({ scrollPosition }) {
    const imgX = useMotionValue(0);
    const imgParallax = useTransform(imgX, (v) => `${wrap(-50, 50, v)}%`);

    useEffect(() => {
        const updateImgX = () => {
            imgX.set((scrollPosition / 600)); // Adjust the baseVelocity as needed
        };

        updateImgX();

        window.addEventListener("scroll", updateImgX);

        return () => {
            window.removeEventListener("scroll", updateImgX);
        };
    }, [scrollPosition, imgX]);

    return (
        <section>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={-10}>Hover Mouse</ParallaxText>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={10}>On Image</ParallaxText>
            <motion.img
                src="./astronaut.png?as=webp"
                style={{ x: imgParallax}}
            />
        </section>
    );
}
