import React, { useState, useEffect, useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import {
    Container,
    Typography,
    Box,
    useTheme,
    Button,
} from "@mui/material";

const Single = ({ project }) => {
    const theme = useTheme();

    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    // useEffect(() => {
    //     const intervalId = setInterval(() => {
    //         setCurrentImageIndex((prevIndex) => (prevIndex + 1) % project.length);
    //     }, 2000);

    //     return () => clearInterval(intervalId);
    // }, []);

    const ref = useRef();

    const { scrollYProgress } = useScroll();
    const y = useTransform(scrollYProgress, [0, 1], [-300, 300]);

    return (
        <Container >
            <Box ref={ref} style={{ width: '100%'}}>
                <Box >
                    <Box>
                        <img
                            alt={project.title}
                            src={
                                project.imageUrls[currentImageIndex] ||
                                project.imageUrls[0]
                            }
                            style={{
                                minHeight: "0",
                                minWidth: "0",
                                maxHeight: "50vh",
                                maxWidth: "80%",
                                border: "1px solid #65f9af",
                                borderRadius: "10px",
                                // opacity: isHovered ? 0.1 : 1,
                            }}
                        />
                        </Box>
                        <motion.div style={{ y }}>

                    {/* {isHovered && */}
                        <Box
                            sx={{
                                textAlign: "center",
                                // position: "absolute",
                            }}
                        >
                            <Typography
                                color={'#FFF'}
                                fontWeight={"bold"}
                                textAlign={"center"}
                                mb={2}
                                sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
                            >
                                {project.title}
                            </Typography>
                            <Typography
                                color={'#FFF'}
                                fontWeight={"bold"}
                                textAlign={"center"}
                                variant="body2"
                                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                            >
                                {project.role}
                            </Typography>
                            <Typography
                                color={'#FFF'}
                                variant="body2"
                                textAlign={"center"}
                                mb={2}
                                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                            >
                                {project.description}
                            </Typography>
                            <Box sx={{ marginTop: "auto", textAlign: "center" }}>
                                {project.demoLink ? (
                                    <Button
                                        href={project.demoLink}
                                        variant="contained"
                                        target="_blank"
                                        sx={{ fontSize: { xs: "8px", sm: "14px" }, backgroundColor: '#65f9af', color: '#327C57' }}
                                    >
                                        Demo
                                    </Button>
                                ) : (
                                    <Typography
                                        variant="body2"
                                        color={'#FFF'}
                                        textAlign={"center"}
                                        mb={2}
                                        sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                                    >
                                        Demo Not Available
                                    </Typography>
                                )}
                            </Box>
                        </Box>
                        </motion.div>
                    {/* } */}
                </Box>
            </Box>

        </Container >
    );
};

export default Single;
