import React, { useState } from 'react';
import { useTransform, useScroll } from 'framer-motion';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
    const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });
    const { scrollYProgress } = useScroll();

    // Infinite loop for x value
    const x = useTransform(
        scrollYProgress,
        [0, 0.5, 1],
        [0, 600, 0],
        { clamp: false }
    );

    const handleMouseMove = (e) => {
        const boundingBox = e.currentTarget.getBoundingClientRect();
        const mouseX = e.clientX - boundingBox.left - boundingBox.width / 2;
        const mouseY = e.clientY - boundingBox.top - boundingBox.height / 2;

        setCirclePosition({ x: mouseX, y: mouseY });
    };

    const handleMouseLeave = () => {
        setCirclePosition({ x: 0, y: 0 });
    };

    return (
        <>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    position: 'relative',
                }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                <img
                    alt={skill.name}
                    style={{ maxWidth: '100%', height: '10vh', objectFit: 'cover', x }}
                    src={skill.image}
                />
                {circlePosition.x !== 0 && (
                    <div
                        style={{
                            position: 'absolute',
                            left: circlePosition.x,
                            top: circlePosition.y,
                            transform: `translate(-50%, -50%)`,
                            width: '10rem',
                            height: '10rem',
                            backgroundColor: '#f9af65',
                            borderRadius: '50%',
                            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', // Add 3D effect with box-shadow
                            animation: 'spin 2s linear infinite', // Add a rotation animation
                        }}
                    >
                        <div
                            style={{
                                marginTop: '45%',
                                color: '#FFF',
                                textAlign: 'center',
                                fontWeight: 'bold',
                            }}
                        >
                            {skill.name}
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default SkillCard;
