import React, { useState, useEffect } from "react";
import Scroll from './Scroll';

import Intro from "../Home/Home";
import Skill from "../Skills/Skill";
import Resume from "../Resume/Resume";
import Project from "../Project/Project";
import Contact from "../Contact/Contact";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Main from "./Main";

import './Wrapper.css';

/**
 * Wrapper component containing the Main component and Scroll component with specified sections.
 */
const Wrapper = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    const handleScroll = () => {
        // Calculate the active section based on scroll position
        const scrollPosition = window.scrollY;
        const sectionHeights = Array.from(document.querySelectorAll('section')).map(section => section.clientHeight);
        const index = sectionHeights.findIndex((height, i) => scrollPosition < height * (i + 1));

        setActiveIndex(index === -1 ? sectionHeights.length - 1 : index);
    };

    useEffect(() => {
        // Add scroll event listener when component mounts
        window.addEventListener('scroll', handleScroll);

        // Clean up the event listener when the component unmounts
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []); // Empty dependency array ensures the effect runs only once on mount

    const scrollToComponent = (index) => {
        if (scrollRef.current) {
            const componentHeight = scrollRef.current.clientHeight;
            scrollRef.current.scrollTo({
                top: index * componentHeight,
                behavior: "smooth",
            });
        }
    };

    const updateSlideState = (index) => {
        scrollToComponent(index);
    };
    return (
        <>
        <Header activeIndex={activeIndex}/>
            <Main>
                <section>
                    <Intro activeIndex={activeIndex}/>
                </section>
                <section>
                    <Skill />
                </section>
                <section>
                    <Project />
                </section>
                <section>
                    <Resume />
                </section>
                <section>
                    <Contact />
                </section>
            </Main>
            {/* <Footer/> */}
        </>
    );
};

export default Wrapper;
