import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import './skill.css';

const SkillCard = ({ skill }) => {
    const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });

    const handleMouseMove = (e) => {
        const boundingBox = e.currentTarget.getBoundingClientRect();
        const mouseX = e.clientX - boundingBox.left - boundingBox.width / 2;
        const mouseY = e.clientY - boundingBox.top - boundingBox.height / 2;

        setCirclePosition({ x: mouseX, y: mouseY });
    };

    const handleMouseLeave = () => {
        setCirclePosition({ x: 0, y: 0 });
    };

    const circleStyle = {
        position: 'absolute',
        left: circlePosition.x,
        top: circlePosition.y,
        transform: `translate(-50%, -50%)`,
        width: '10rem',
        height: '10rem',
    };

    return (
        <>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    position: 'relative',
                }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                <img
                    alt={skill.name}
                    style={{ maxWidth: '100%', height: '10vh', objectFit: 'cover' }}
                    src={skill.image}
                />
                {circlePosition.x !== 0 && (
                    <div className="animated-circle" style={circleStyle}>
                        <div
                            className="animated-text"
                            style={{
                                marginTop: '45%',
                                color: '#FFF',
                                textAlign: 'center',
                                fontWeight: 'bold',
                            }}
                        >
                            {skill.name}
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default SkillCard;
