import React, { useEffect, useState } from "react";
import Header from "../Header";
import Intro from "../Intro";
import Skill from "../Skill";
import Resume from "../Resume";
import Project from "../Project";
import Contact from "../Contact";
import Footer from "../Footer";
import Main from "../Main";

const Home = () => {
  const [currentSection, setCurrentSection] = useState("Intro");

  const handleScroll = () => {
    // 현재 스크롤 위치를 가져오는 코드
    const scrollPosition = window.scrollY || document.documentElement.scrollTop;

    // 각 섹션의 위치를 가져와서 현재 섹션을 업데이트
    if (scrollPosition < Skill.offsetTop) {
      setCurrentSection("Intro");
    } else if (scrollPosition < Project.offsetTop) {
      setCurrentSection("Skill");
    } else if (scrollPosition < Resume.offsetTop) {
      setCurrentSection("Project");
    } else if (scrollPosition < Contact.offsetTop) {
      setCurrentSection("Resume");
    } else {
      setCurrentSection("Contact");
    }
  };

  useEffect(() => {
    // 스크롤 이벤트 리스너 등록
    window.addEventListener("scroll", handleScroll);

    // 컴포넌트가 언마운트될 때 리스너 제거
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Header />
      <Main>
        {currentSection === "Intro" && <Intro />}
        {currentSection === "Skill" && <Skill />}
        {currentSection === "Project" && <Project />}
        {currentSection === "Resume" && <Resume />}
        {currentSection === "Contact" && <Contact />}
      </Main>
      <Footer />
    </>
  );
};

export default Home;
