import React, { useRef } from "react";
import { AppBar, Toolbar, Typography, Button } from "@mui/material";
import Slide from "./Slides";

const Header = () => {
  const containerRef = useRef(null);

  const handleButtonClick = (index) => {
    // Additional logic before updating the slide index
    smoothScrollToSlide(index);

    // Update the slide index when a button is clicked
    updateSlideState(index);
  };

  const smoothScrollToSlide = (index) => {
    // Perform smooth scroll to the clicked slide
    const container = containerRef.current;
    const slideHeight = container.clientHeight;
    const scrollTo = index * slideHeight;

    container.scrollTo({
      top: scrollTo,
      behavior: 'smooth',
    });
  };

  return (
    <AppBar position="fixed" color="transparent" elevation={0}>
      <Toolbar>
        <Typography variant="h5" to="/" sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}>
          PORTFOLIO
        </Typography>
        <Button color="inherit" onClick={() => handleButtonClick(0)}>
          Projects
        </Button>
        <Button color="inherit" onClick={() => handleButtonClick(1)}>
          Resume
        </Button>
        <Button color="inherit" onClick={() => handleButtonClick(2)}>
          Skills
        </Button>
        <Button color="inherit" onClick={() => handleButtonClick(3)}>
          Contact
        </Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
