// Wrapper.jsx

import React from "react";
import Intro from "../Home/Home";
import Skill from "../Skills/Skill";
import Resume from "../Resume/Resume";
import Project from "../Project/Project";
import Contact from "../Contact/Contact";
import Header from "../Header/Header";
import Main from "./Main";

/**
 * Scroll component for handling scrolling and rendering of each section.
 */
const Scroll = ({ children }) => {
  const sectionRefs = React.Children.map(children, () => React.createRef());

  const handleScroll = (index) => {
    sectionRefs[index].current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div>
      {React.Children.map(children, (child, index) =>
        React.cloneElement(child, { ref: sectionRefs[index] })
      )}
      <ul>
        {React.Children.map(children, (child, index) => (
          <li onClick={() => handleScroll(index)}>Section {index + 1}</li>
        ))}
      </ul>
    </div>
  );
};

/**
 * Wrapper component containing the Main component and Scroll component with specified sections.
 */
const Wrapper = () => {
  return (
    <>
      <Main>
        <Scroll>
          <Header />
          <Intro />
          <Skill />
          <Project />
          <Resume />
          <Contact />
        </Scroll>
      </Main>
    </>
  );
};

export default Wrapper;
