import React from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const scrollToNextSection = () => {
    // 스크롤 애니메이션을 통해 다음 섹션으로 이동
    scroller.scrollNext();
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
      onWheel={scrollToNextSection}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
