import React, { useState, useEffect, useCallback } from 'react';
import { Box } from '@mui/material';
import NavBar from './Slides/NavBar';

function Slides(props) {
  const { components } = props;
  const transitionSpeed = 150;

  const [idxState, setIdxState] = useState({
    currVIdx: 0,
    pastVIdx: 0,
    currHIdx: [],
    pastHIdx: [],
    isSwitching: false,
  });

  let scrollTimeout;
  let acceptScroll = true;
  let deltaY = 0;
  let deltaX = 0;
  let prevX = -1;
  let prevY = -1;

  const isHorizontal = useCallback((slide) => slide?.length !== undefined, []);

  useEffect(() => {
    setIdxState((prev) => ({
      ...prev,
      currHIdx: components.map(() => 0),
      pastHIdx: components.map(() => 0),
    }));
  }, [components]);

  const vIdxSetter = useCallback(
    (newIdx) => {
      setIdxState((prev) => ({
        ...prev,
        pastHIdx: [...prev.currHIdx],
        pastVIdx: prev.currVIdx,
        isSwitching: true,
      }));

      setTimeout(() => {
        setIdxState((prev) => ({
          ...prev,
          isSwitching: false,
          currVIdx: newIdx,
        }));
      }, transitionSpeed);
    },
    [transitionSpeed]
  );

  const hIdxSetter = useCallback(
    (newIdx) => {
      setIdxState((prev) => ({
        ...prev,
        pastVIdx: prev.currVIdx,
        // pastHIdx: [...prev.pastHIdx],
        pastHIdx: prev.pastHIdx.map((item, index) =>
          index === prev.currVIdx ? newIdx : item
        ),
        isSwitching: true,
      }));

      setTimeout(() => {
        setIdxState((prev) => ({
          ...prev,
          isSwitching: false,
          currHIdx: prev.currHIdx.map((item, index) =>
            index === prev.currVIdx ? newIdx : item
          ),
        }));
      }, transitionSpeed);
    },
    [transitionSpeed]
  );

  const scrollReset = () => {
    scrollTimeout = undefined;
    acceptScroll = true;
    deltaY = 0;
    deltaX = 0;
    prevY = -1;
    prevX = -1;
  };

  const scrollTimeoutEvent = () => {
    if (Math.abs(deltaY) >= Math.abs(deltaX)) {
      if (deltaY > 50 && idxState.currVIdx < components.length - 1) {
        vIdxSetter(idxState.currVIdx + 1);
      } else if (deltaY < -50 && idxState.currVIdx > 0) {
        vIdxSetter(idxState.currVIdx - 1);
      }
    } else {
      if (idxState.currHIdx[idxState.currVIdx] !== -1) {
        if (
          deltaX > 50 &&
          idxState.currHIdx[idxState.currVIdx] <
            (components[idxState.currVIdx] || []).length - 1
        ) {
          hIdxSetter(idxState.currHIdx[idxState.currVIdx] + 1);
        } else if (deltaX < -50 && idxState.currHIdx[idxState.currVIdx] > 0) {
          hIdxSetter(idxState.currHIdx[idxState.currVIdx] - 1);
        }
      }
    }

    setTimeout(scrollReset, 300);
  };

  const scrollEventHandler = (e) => {
    if (acceptScroll) {
      if (scrollTimeout) {
        clearTimeout(scrollTimeout);
      }
      scrollTimeout = setTimeout(scrollTimeoutEvent, 300);

      if (e.type === 'wheel') {
        deltaY += e.deltaY;
        deltaX += e.deltaX;
      } else {
        const y = e.targetTouches[0].clientY;
        const x = e.targetTouches[0].clientX;

        if (prevY !== -1 || prevX !== -1) {
          deltaY += prevY - y;
          deltaX += prevX - x;
        }

        prevY = y;
        prevX = x;
      }
    }
  };

  const slideStyle = {
    width: '100%',
    height: '100%',
    position: 'fixed',
  };

  return (
    <Box
      sx={{ overscrollBehavior: 'none' }}
      onWheel={scrollEventHandler}
      onTouchMove={scrollEventHandler}
    >
      <NavBar
        orientation="vertical"
        length={components.length}
        currIdx={idxState.currVIdx}
        clickHandler={vIdxSetter}
      />
      {isHorizontal(components[idxState.currVIdx]) && (
        <NavBar
          orientation="horizontal"
          length={(components[idxState.currVIdx] || []).length}
          currIdx={idxState.currHIdx[idxState.currVIdx]}
          clickHandler={hIdxSetter}
        />
      )}
      <Box
        sx={{
          ...slideStyle,
          zIndex: 3,
          transition: `all ${transitionSpeed}ms linear 0ms`,
          opacity: idxState.isSwitching ? 1 : 0,
        }}
        visibility={idxState.isSwitching ? undefined : 'hidden'}
      >
        {isHorizontal(components[idxState.pastVIdx]) ? (
          (components[idxState.pastVIdx] || [])[
            idxState.pastHIdx[idxState.pastVIdx]
          ]
        ) : (
          components[idxState.pastVIdx]
        )}
      </Box>
      <Box sx={{ ...slideStyle, zIndex: 2 }}>
        {isHorizontal(components[idxState.currVIdx]) ? (
          (components[idxState.currVIdx] || [])[
            idxState.currHIdx[idxState.currVIdx]
          ]
        ) : (
          components[idxState.currVIdx]
        )}
      </Box>
    </Box>
  );
}

export default Slides;
