import React, { useState, useEffect } from "react";
import Scroll from './Scroll';

import Intro from "../Home/Home";
import Skill from "../Skills/Skill";
import Resume from "../Resume/Resume";
import Project from "../Project/Project";
import Contact from "../Contact/Contact";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Main from "./Main";

import './Wrapper.css';

const Wrapper = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [maxScrollPosition, setMaxScrollPosition] = useState(0);

    const handleScroll = () => {
        const scrollPosition = window.scrollY;
        const sectionHeights = Array.from(document.querySelectorAll('section')).map(section => section.clientHeight);
        const index = sectionHeights.findIndex((height, i) => scrollPosition < height * (i + 1));

        setActiveIndex(index === -1 ? sectionHeights.length - 1 : index);
        setMaxScrollPosition(sectionHeights.reduce((acc, height) => acc + height, 0) - window.innerHeight);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div style={{ overflowY: window.scrollY >= maxScrollPosition ? "hidden" : "scroll" }}>
            <Header activeIndex={activeIndex}/>
            <Main>
                <section>
                    <Intro activeIndex={activeIndex}/>
                </section>
                <section>
                    <Skill />
                </section>
                <section>
                    <Project />
                </section>
                <section>
                    <Resume />
                </section>
                <section>
                    <Contact />
                </section>
            </Main>
        </div>
    );
};

export default Wrapper;
