import React, { useState, useEffect } from "react";
import { FullPage, Slide } from 'react-full-page';
import Header from "../Header";
import Intro from "../Intro";
import Skill from "../Skill";
import Resume from "../Resume";
import Project from "../Project";
import Contact from "../Contact";
import Footer from "../Footer";

const Home = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const slideTimeout = setTimeout(() => {
            if (currentSlide < 4) {  // Adjust the number based on the total number of slides
                setCurrentSlide(currentSlide + 1);
            }
        }, 1000);  // Adjust the delay time in milliseconds

        return () => clearTimeout(slideTimeout);
    }, [currentSlide]);

    return (
        <>
            <Header />
            <FullPage controls>
                <Slide>
                    {currentSlide === 0 && <Intro />}
                </Slide>
                <Slide>
                    {currentSlide === 1 && <Skill />}
                </Slide>
                <Slide>
                    {currentSlide === 2 && <Project />}
                </Slide>
                <Slide>
                    {currentSlide === 3 && <Resume />}
                </Slide>
                <Slide>
                    {currentSlide === 4 && <Contact />}
                </Slide>
            </FullPage>
            <Footer />
        </>
    );
};

export default Home;
