import { motion, useSpring, useTransform, useMotionValue, useVelocity, useScroll } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography, useTheme, useMediaQuery } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100 }) {
    const baseX = useMotionValue(0);
    const { scrollY } = useScroll();
    const scrollVelocity = useVelocity(scrollY);
    const smoothVelocity = useSpring(scrollVelocity, {
        damping: 20,
        stiffness: 200
    });
    const velocityFactor = useTransform(smoothVelocity, [0, 1], [2, 0], {
        clamp: false
    });

    const direction = useTransform(smoothVelocity, (latest) => latest > 0 ? 1 : -1);

    const x = useTransform(baseX, (v) => `${wrap(-50, 50, v)}%`);
    const adjustedX = useTransform([x, direction], (xValue, dir) => xValue * dir);

    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <motion.div style={{ x: adjustedX, width: isSmallScreen ? '' : '200rem' }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h1" style={{ fontSize: isSmallScreen ? "3em" : "5.5rem", textAlign: "center", }}>
                    {children}
                </Typography>
            </motion.div>
        </div>
    );
}

export default function ProSkill() {
    return (
        <section>
            <ParallaxText baseVelocity={-5}>LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S</ParallaxText>
            <ParallaxText baseVelocity={5}>START START START START START START START START START START START START START START START START START START START START</ParallaxText>
        </section>
    );
}
