// App.jsx
/*
SPDX-FileCopyrightText: © 2024 Heewon Kim <khw0285@gmail.com>
SPDX-License-Identifier: MIT
*/

import React,{useCallback} from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Particles from "react-particles";
import { loadFull } from "tsparticles";

import Wrapper from './components/Wrapper/Wrapper'

const App = () => {
    const options = {
        background: {
         color: "#fff",
       },
       particles: {
         shape: {
           type: "circle"
         },
         number: {
           value: 0
         },
         color: {
           value: "random"
         },
         opacity: {
           value: 0.3
         },
         size: {
           value: {min: 5, max: 20}
         },
         move: {
           enable: true,
           speed: 6,
           random: false,
           outModes: "bounce",
         }
       },
       absorbers: [
         {
           position: { x: 50, y: 50 },
           size: {
             value: 50,
             limit: 100,
           }
         }
       ],
       emitters: [
         {
           direction: "top-right",
           position: {
             x: 0,
             y: 100
           },
           rate: {
             delay: 0.3,
             quantity: 4,
           },
         },
       ]
     };
     const particlesInit = useCallback(async (engine) => {
        await loadFull(engine);
      }, []);
    return (
    <BrowserRouter>
        <Routes>
        <Particles options={options} init={particlesInit} />
            <Routes>
  <Route path="/" element={<><Particles options={options} init={particlesInit} /><Wrapper /></>} />
</Routes>
        </Routes>
    </BrowserRouter>
    );
};

export default App;
