const Resume = () => {
  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 1500 },
  });

  return (
    <Container id="resume" style={{ textAlign: "center" }}>
      <Typography variant="h2" mt={5} mb={3} style={{ fontSize: "2em" }}>
        Resume
      </Typography>

      <Typography variant="h4" mt={3} mb={2} style={{ fontSize: "1.5em" }}>
        Experience
      </Typography>
      <Divider mb={3} />

      {experienceData.map((experience, index) => (
        <AnimatedBox key={index} style={fadeIn} mb={3}>
          <Typography variant="h6" style={{ fontSize: "1.2em" }}>
            {experience.title}
          </Typography>
          <Typography variant="subtitle2" color="textSecondary" style={{ fontSize: "1em" }}>
            {experience.company} | {experience.year}
          </Typography>
          {experience.description.map((point, i) => (
            <Typography key={i} variant="body1" style={{ fontSize: "1em" }}>
              {point}
            </Typography>
          ))}
        </AnimatedBox>
      ))}

      <Typography variant="h4" mt={5} mb={2} style={{ fontSize: "1.5em" }}>
        Education
      </Typography>
      <Divider mb={3} />

      {educationData.map((education, index) => (
        <AnimatedBox key={index} style={fadeIn} mb={3}>
          <Typography variant="h6" style={{ fontSize: "1.2em" }}>
            {education.degree}
          </Typography>
          <Typography variant="subtitle2" color="textSecondary" style={{ fontSize: "1em" }}>
            {education.school} | {education.year}
          </Typography>
        </AnimatedBox>
      ))}
    </Container>
  );
};

export default Resume;
