import React, { useState } from "react";
import { Container, Typography, Card, CardContent, CardMedia, Grid, Button } from "@mui/material";

const projectsData = [
    {
        title: "College Mate App",
        description: "CollegeMate is a college community app designed exclusively for University of Wisconsin-Madison students.",
        imageUrls: ["./CollegeMate.png?as=webp"],
        demoLink: "https://collegemate.app/",
    },
    {
        title: "Wisconsin State Cartographer's Office",
        description: "Simplify survey note retrieval by implementing an intuitive map-based interface, eliminating the need for specialized township knowledge and greatly enhancing user accessibility.",
        imageUrls: ["./SCOmap.png?as=webp", "./SCOgithub.png?as=webp"],
        demoLink: "#",
    },
    {
        title: "Project B",
        description: "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
        imageUrls: ["https://via.placeholder.com/300"],
        demoLink: "#",
    },
];

const Project = () => {
    const [randomProjectIndex, setRandomProjectIndex] = useState(null);

    const getRandomIndex = () => {
        const index = Math.floor(Math.random() * projectsData.length);
        setRandomProjectIndex(index);
    };

    return (
        <Container
            id="intro"
            sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                height: "100%",
            }}
        >
            <Typography variant="h2" fontWeight={"bold"} align="center" mb={5}>
                Projects
            </Typography>

            <Button onClick={getRandomIndex} variant="contained" color="primary" mb={3}>
                Show Random Project
            </Button>

            <Grid container spacing={3}>
                {randomProjectIndex !== null && (
                    <Grid item xs={12} sm={6} md={4}>
                        <Card elevation={3}>
                            <CardMedia
                                component="img"
                                alt={projectsData[randomProjectIndex].title}
                                height="140"
                                image={projectsData[randomProjectIndex].imageUrls[0]}
                            />
                            <CardContent>
                                <Typography variant="h6" fontWeight={"bold"} textAlign={"center"}>
                                    {projectsData[randomProjectIndex].title}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" textAlign={"center"} mb={5}>
                                    {projectsData[randomProjectIndex].description}
                                </Typography>
                                <Button
                                    href={projectsData[randomProjectIndex].demoLink}
                                    variant="contained"
                                    color="primary"
                                    target="_blank"
                                >
                                    Demo
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                )}
            </Grid>
        </Container>
    );
};

export default Project;
