import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
    const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });

    const handleMouseMove = (e) => {
        const x = e.clientX;
        const y = e.clientY;

        // Calculate half of the circle's size
        const halfCircleSize = 20 / 2; // Assuming circle size is 20vh

        // Calculate adjusted coordinates to center the circle on the mouse
        let adjustedX = x - halfCircleSize;
        let adjustedY = y - halfCircleSize;

        // Ensure the circle stays within the bounds of the image
        const maxX = window.innerWidth - 50; // Assuming circle size is 20vh
        const maxY = window.innerHeight - 20; // Assuming circle size is 20vh

        adjustedX = Math.max(0, Math.min(adjustedX, maxX));
        adjustedY = Math.max(0, Math.min(adjustedY, maxY));

        setCirclePosition({ x: adjustedX, y: adjustedY });
    };

    const handleMouseLeave = () => {
        setCirclePosition({ x: 0, y: 0 });
    };

    return (
        <>
            <div
                style={{ position: 'relative', display: 'inline-block' }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                {/* 이미지를 감싸는 div를 추가하고 그 안에 이미지를 렌더링합니다. */}
                <img
                    alt={skill.name}
                    style={{ maxWidth: "100%", height: "10vh", objectFit: "cover" }}
                    src={skill.image}
                />

                {/* 동그라미를 표시할 div */}
                <div
                    style={{
                        position: 'absolute',
                        top: `${circlePosition.y}px`,
                        left: `${circlePosition.x}px`,
                        // transform: 'translate(-50%, -50%)',
                        width: '20vh',
                        height: '20vh',
                        borderRadius: '50%',
                        backgroundColor: 'white',
                        border: '1px solid #FFF',
                        display: circlePosition.x === 0 && circlePosition.y === 0 ? 'none' : 'block',
                    }}
                >{skill.name}</div>
            </div>
        </>
    );
};

export default SkillCard;
