import React, { useRef } from "react";
import SkillCard from "./SkillCard";
import {
  Container,
  Typography,
  Grid,
  useTheme,
  useMediaQuery,
  responsiveFontSizes,
  ThemeProvider,
} from "@mui/material";
import { useSpring, animated, config } from "@react-spring/web";

const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const Skill = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);

  const getRandomPosition = () => ({
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
  });

  const getPositionSpring = (x, y) => useSpring({
    to: async (next) => {
      await next({ x, y, immediate: true });
      await next({ x: 0, y: 0, immediate: false, config: config.default });
    },
  });

  return (
    <ThemeProvider theme={responsiveTheme}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography
          fontWeight={"bold"}
          variant="h2"
          mb={3}
          style={{
            fontSize: isSmallScreen ? "1em" : "3em",
            textAlign: "center",
          }}
        >
          Skills
        </Typography>

        <Grid container spacing={1}>
          {skillsData.map((category, index) => (
            <Grid item key={index} xs={4} sm={6} md={4}>
              <Typography
                color={"GrayText"}
                mb={2}
                textAlign={"center"}
                sx={{ fontSize: { xs: "12px", sm: "14px", md: "20px" } }}
              >
                {category.category}
              </Typography>
              <Grid container spacing={1}>
                {category.skills.map((skill, skillIndex) => (
                  <Grid
                    item
                    key={skillIndex}
                    xs={6}
                    sm={4}
                    md={6}
                    style={{ position: "relative" }}
                  >
                    <animated.div {...getPositionSpring(getRandomPosition().x, getRandomPosition().y)}>
                      <SkillCard skill={skill} />
                    </animated.div>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default Skill;
