import React from "react";
import SkillCard from "./SkillCard";
import {
  Container,
  Typography,
  Grid,
  useTheme,
  useMediaQuery,
  responsiveFontSizes,
  ThemeProvider,
} from "@mui/material";
import { useSpring } from "@react-spring/web";

const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const Skill = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);

  const getRandomPosition = () => ({
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
  });

  const getCorrectPosition = (index) => {
    // Calculate the correct position based on the grid structure
    const gridSize = isSmallScreen ? 2 : 3;
    const col = index % gridSize;
    const row = Math.floor(index / gridSize);

    const cellWidth = window.innerWidth / gridSize;
    const cellHeight = window.innerHeight / gridSize;

    const correctX = col * cellWidth + cellWidth / 2;
    const correctY = row * cellHeight + cellHeight / 2;

    return { x: correctX, y: correctY };
  };

  const animatedStyles = useSpring({
    from: { x: 0, y: 0 },
    to: async (next) => {
      await next(getRandomPosition()); // Start from random position
      await next(getCorrectPosition(0)); // Move to correct position
    },
    reset: true,
  });

  return (
    <ThemeProvider theme={responsiveTheme}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography
          fontWeight={"bold"}
          variant="h2"
          mb={3}
          style={{
            fontSize: isSmallScreen ? "1em" : "3em",
            textAlign: "center",
          }}
        >
          Skills
        </Typography>

        <Grid container spacing={1}>
          {skillsData.map((category, index) => (
            <Grid item key={index} xs={4} sm={6} md={4}>
              <Typography
                color={"GrayText"}
                mb={2}
                textAlign={"center"}
                sx={{ fontSize: { xs: "12px", sm: "14px", md: "20px" } }}
              >
                {category.category}
              </Typography>
              <Grid container spacing={1}>
                {category.skills.map((skill, skillIndex) => (
                  <Grid
                    item
                    key={skillIndex}
                    xs={6}
                    sm={4}
                    md={6}
                    style={{
                      position: "relative",
                      transform: `translate(${animatedStyles.x}px, ${animatedStyles.y}px)`,
                    }}
                  >
                    <SkillCard skill={skill} />
                  </Grid>
                ))}
              </Grid>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default Skill;
