import React from "react";
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";

const skillsData = [
  { category: "Programming Skills", skills: ["JavaScript", "React.js", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup and Stylesheet Languages", skills: ["HTML5", "CSS3", "HTML5", "CSS"] },
  { category: "Database and Data Format", skills: ["MySQL", "JSON"] },
  { category: "Frameworks", skills: ["React-Native", "FastAPI"] },
  { category: "Design Tool", skills: ["Figma"] },
  { category: "Tools and Practices", skills: ["Postman", "Git", "Github", "Linux"] },
];

const Skill = () => (
  <Container
    id="intro"
    sx={{
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      height: "100%",
    }}
  >
    <Typography variant="h2" fontWeight={"bold"} align="center" mb={10}>
      Skills
    </Typography>

    {skillsData.map((category, index) => (
      <div key={index}>
        <Typography variant="h4" mb={2}>
          {category.category}
        </Typography>
        <Grid container spacing={2}>
          {category.skills.map((skill, skillIndex) => (
            <Grid item key={skillIndex} xs={6} sm={4} md={3}>
              <Card>
                <CardContent>
                  <Typography variant="body1">{skill}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </div>
    ))}
  </Container>
);

export default Skill;
