import React, { useMemo } from 'react';
import { AppBar, Toolbar, Typography, Button } from "@mui/material";
import Slides from "./Home/Slides";
import Intro from "./Intro";
import Skill from "./Skill";
import Resume from "./Resume";
import Project from "./Project";
import Contact from "./Contact";
import NavDot from "./Home/Slides/NavDot";

const Header = ({ orientation, length, currIdx, clickHandler }) => {
  const style = useMemo(() => ({
    position: 'fixed',
    display: 'flex',
    flexDirection: orientation === 'vertical' ? 'column' : 'row',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '10px',
    transform: orientation === 'vertical' ? 'translateY(-50%)' : 'translateX(-50%)',
    zIndex: 1000,
  }), [orientation]);

  const navigationButtons = [
    { label: 'Intro', component: Intro },
    { label: 'Skills', component: Skill },
    { label: 'Projects', component: Project },
    { label: 'Resume', component: Resume },
    { label: 'Contact', component: Contact },
  ];

  return (
    <AppBar position="fixed" color="transparent" elevation={0}>
      <Toolbar>
        <Typography variant="h5" sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}>
          PORTFOLIO 
        </Typography>
        {navigationButtons.map(({ label, component: Component }, idx) => (
          <React.Fragment key={idx}>
            <Button color="inherit" onClick={() => clickHandler(idx)}>
              {label}
            </Button>
            <NavDot
              isCurrent={currIdx === idx}
              thisIdx={idx}
              clickHandler={() => clickHandler(idx)}
              visibi
            />
          </React.Fragment>
        ))}
      </Toolbar>
    </AppBar>
  );
};

export default Header;
