SPDX-License-Identifier: MIT
*/

import React from "react";

import { Timeline } from "@mui/lab";
import {
    Container,
    Typography,
    Divider,
    Box,
    Button,
    useMediaQuery,
    useTheme,
    responsiveFontSizes,
    ThemeProvider
} from "@mui/material";
import SchoolIcon from '@mui/icons-material/School';
import DevicesIcon from '@mui/icons-material/Devices';

import TimeLineCard from "./TimeLineCard";


// const experienceData = [
//     {
//         title: "Back-end Developer",
//         company: "Wisconsin State Cartographer's Office, Madison, WI",
//         year: "Sept 2023 - Dec 2023",
//         description: [
//             "Simplified survey note retrieval through an intuitive map-based interface, enhancing user accessibility.",
//             "Click DOWNLOAD RESUME for more information!",
//         ],
//     },
//     {
//         title: "Co-Founder & Front-end Developer",
//         company: "College Mate App, Madison, WI",
//         year: "Oct 2022 - Present",
//         description: [
//             "Front-end development of the College Mate App to build a connected community among University of Wisconsin–Madison students.",
//             "Click DOWNLOAD RESUME for more information!",
//         ],
//     },
// ];

// const educationData = [
//     {
//         degree: "Bachelor's in Computer Science",
//         school: "University of Wisconsin - Madison",
//         year: "Jan 2021 - Dec 2023",
//     },
//     {
//         degree: "Associate's",
//         school: "Madison Area Technical College",
//         year: "Sept 2019 - Dec 2020",
//     },
// ];

// ({ time, activity, details, icon }) 
const timeLineData = [
    {
        id: 1,
        time: "Sept 2019 - Dec 2020",
        activity: "Madison Area Technical College",
        details: "Associate's",
        icon: SchoolIcon,
    },
    {
        id: 2,
        time: "Jan 2021 - Dec 2023",
        activity: "University of Wisconsin - Madison",
        details: "Bachelor's in Computer Science",
        icon: SchoolIcon,
    },
    {
        id: 3,
        time: "Oct 2022 - Present",
        activity: "College Mate App, Madison, WI",
        details: "Co-Founder & Front-end Developer",
        icon: DevicesIcon,
    },
    {
        id: 4,
        time: "Sept 2023 - Dec 2023",
        activity: "Wisconsin State Cartographer's Office, Madison, WI",
        details: "Back-end Developer",
        icon: DevicesIcon,
    },

]

/**
 * Resume component displaying experiences and education with download option.
 */
const Resume = () => {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
    const responsiveTheme = responsiveFontSizes(theme);

    const handleDownload = () => {
        const resumeFileUrl = "./resume.pdf";

        // Open the PDF file in a new tab
        window.open(resumeFileUrl, "_blank");
    };

    return (
        <ThemeProvider theme={responsiveTheme}>
            <Container>
                <Typography fontWeight={"bold"} variant="h2" mb={3} style={{ fontSize: isSmallScreen ? "1.5em" : "3em",  }}>
                    RESUME
                </Typography>

                <Timeline >
                    {timeLineData.map((timeline) => (
                        <TimeLineCard key={timeline.id} {...timeline} />
                    ))}
                </Timeline>



                <Typography variant="subtitle1" mt={2} style={{ fontSize: isSmallScreen ? "0.75em" : "1.2em" }}>
                    Want more information?
                </Typography>

                <Button variant="outlined" onClick={handleDownload} style={{ marginBottom: isSmallScreen ? "10px" : "20px" }}>
                    Download Resume
                </Button>

            </Container>
        </ThemeProvider>
    );
};

export default Resume;
