import React, { useState } from "react";
import { Container, Typography, Card, CardContent, Grid, LinearProgress, Dialog, DialogTitle, DialogContent, DialogActions, Button } from "@mui/material";

// Frontend Skills
const frontendSkills = [
    { title: "JavaScript", level: 90 },
    { title: "React.js", level: 85 },
    { title: "HTML5", level: 90 },
    { title: "CSS3", level: 85 },
    { title: "HTML5", level: 95 },
    { title: "CSS", level: 100 },
    { title: "Figma", level: 90 },
];

// Backend Skills
const backendSkills = [
    { title: "Java", level: 90 },
    { title: "C", level: 75 },
    { title: "Python", level: 65 },
    { title: "TypeScript", level: 70 },
    { title: "R", level: 80 },
    { title: "MySQL", level: 85 },
    { title: "React-Native", level: 85 }, // Note: React-Native can be used for mobile (frontend) or backend
    { title: "FastAPI", level: 60 },
    { title: "JSON", level: 90 },
    { title: "Node.js", level: 90 },
];

// Common Skills (Tools and Practices)
const commonSkills = [
    { title: "Postman", level: 80 },
    { title: "Git", level: 95 },
    { title: "Linux", level: 70 },
];

  // Skill Component
  const Skill = () => {

    return (
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography variant="h2" fontWeight={"bold"} align="center" mb={10}>
          Skills
        </Typography>
        <Grid container spacing={3}>
          {/* Frontend Skills */}
          <SkillCard
            category="Frontend Skills"
            averageLevel={calculateAverageLevel(frontendSkills)}
            onClick={() =>
              handleCardClick("Frontend Skills", calculateAverageLevel(frontendSkills))
            }
          />

          {/* Backend Skills */}
          <SkillCard
            category="Backend Skills"
            averageLevel={calculateAverageLevel(backendSkills)}
            onClick={() =>
              handleCardClick("Backend Skills", calculateAverageLevel(backendSkills))
            }
          />

          {/* Common Skills */}
          <SkillCard
            category="Common Skills"
            averageLevel={calculateAverageLevel(commonSkills)}
            onClick={() =>
              handleCardClick("Common Skills", calculateAverageLevel(commonSkills))
            }
          />
        </Grid>

        <Typography variant="body1" align="center" color="textSecondary" mt={5} paragraph>
          Click cards to see details about skills
        </Typography>

        {/* Modal */}
        <Dialog open={openModal} onClose={handleCloseModal} maxWidth="sm" fullWidth>
          <DialogTitle>{selectedSkill && selectedSkill.category}</DialogTitle>
          <DialogContent>
            {selectedSkill && (
              <>
                <ul>
                  {selectedSkill.category === "Frontend Skills" &&
                    frontendSkills.map((skill) => (
                      <li key={skill.title}>
                        {skill.title}: {skill.level}%
                      </li>
                    ))}
                  {selectedSkill.category === "Backend Skills" &&
                    backendSkills.map((skill) => (
                      <li key={skill.title}>
                        {skill.title}: {skill.level}%
                      </li>
                    ))}
                  {selectedSkill.category === "Common Skills" &&
                    commonSkills.map((skill) => (
                      <li key={skill.title}>
                        {skill.title}: {skill.level}%
                      </li>
                    ))}
                </ul>
              </>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseModal}>Close</Button>
          </DialogActions>
        </Dialog>
      </Container>
    );
  };

  export default Skill;
