import React from "react";
import SkillCard from "./SkillCard";
import { Container, Typography, Grid, useTheme, useMediaQuery } from "@mui/material";

const Skill = () => {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <Container
            id="intro"
            sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                height: "100%",
            }}
        >
            <Typography variant="h2" fontWeight={"bold"} align="center" mb={5}>
                Skills
            </Typography>

            <Grid container spacing={isSmallScreen ? 0 : 1}>
                {skillsData.map((category, index) => (
                    <Grid item key={index} xs={12} sm={6} md={4}>
                        <Typography variant={isSmallScreen ? "h5" : "h6"} color={"GrayText"} mb={2} textAlign={"center"}>
                            {category.category}
                        </Typography>
                        <Grid container spacing={isSmallScreen ? 0 : 1}>
                            {category.skills.map((skill, skillIndex) => (
                                <Grid item key={skillIndex} xs={isSmallScreen ? 6 : 5} sm={4} md={6}>
                                    <SkillCard skill={skill} />
                                </Grid>
                            ))}
                        </Grid>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Skill;
