import React from 'react';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
    const cardStyle = {
        position: 'relative',
        display: 'inline-block',
    };

    const imageStyle = {
        maxWidth: '100%',
        height: '10vh',
        objectFit: 'cover',
    };

    const hoverEffectStyle = {
        content: '',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.5)', // Adjust the color and opacity as needed
        borderRadius: '50%',
    };

    return (
        <div style={cardStyle} className="skill-card">
            {/* img 요소를 사용하여 이미지를 추가합니다. */}
            <img
                alt={skill.name}
                style={imageStyle}
                src={skill.image}  // skill 객체에 이미지 경로 또는 URL을 가리키는 속성을 추가해야 합니다.
            />
            <div style={hoverEffectStyle} />
        </div>
    );
};

export default SkillCard;
