import { useRef } from "react";
import { motion, useSpring, useTransform, useMotionValue, useVelocity, useAnimationFrame, useScroll } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100 }) {
  const baseX = useMotionValue(0);
  const { scrollY } = useScroll();
  const scrollVelocity = useVelocity(scrollY);
  const smoothVelocity = useSpring(scrollVelocity, {
    damping: 20,
    stiffness: 200
  });
  const velocityFactor = useTransform(smoothVelocity, [0, 500], [0, 5], {
    clamp: false
  });

  const x = useTransform(baseX, (v) => `${wrap(100, 0, v)}%`);

  const directionFactor = useRef(1);
  useAnimationFrame((t, delta) => {
    let currentScrollPos = window.scrollY || document.documentElement.scrollTop;

    // 최초 호출 시에는 lastScrollPos를 초기화
    if (lastScrollPos === undefined) {
      lastScrollPos = currentScrollPos;
      return;
    }

    var scrollDistance = currentScrollPos - lastScrollPos;
    var scrollSpeed = 0.02; // 원하는 스크롤 속도 조절 (임의의 값, 필요에 따라 조정)
    var finalScrollDistance = scrollDistance * scrollSpeed;

    lastScrollPos = currentScrollPos;

    console.log("finalScrollDistance", finalScrollDistance);

    // 이하에서 finalScrollDistance를 사용하여 원하는 작업을 수행
    // 예를 들어, baseX.set(baseX.get() + finalScrollDistance);
  });

  return (
    <div className="parallax">
      <motion.div className="scroller" style={{ x:x }}>
        <Typography varient={'h1'} color={"#FFF"}>{children} </Typography>
        <Typography varient={'h1'} color={"#FFF"}>{children} </Typography>
        <Typography varient={'h1'} color={"#FFF"}>{children} </Typography>
        <Typography varient={'h1'} color={"#FFF"}>{children} </Typography>
      </motion.div>
    </div>
  );
}

export default function App() {
  return (
    <section>
      <ParallaxText baseVelocity={-5}>Framer Motion</ParallaxText>
      <ParallaxText baseVelocity={5}>Scroll velocity</ParallaxText>
    </section>
  );
}
