import React from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const scrollToNextSection = () => {
    // 다음 섹션으로 스크롤
    const nextSection = `section${getCurrentSectionIndex() + 1}`;
    scroller.scrollTo(nextSection, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  const getCurrentSectionIndex = () => {
    // 현재 화면에 보이는 섹션의 인덱스를 가져옴
    const scrollPosition = window.scrollY;
    const sectionHeight = window.innerHeight;
    return Math.floor(scrollPosition / sectionHeight);
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
      onWheel={scrollToNextSection}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
