import React, { useState, useRef } from "react";
import Scroll from './Scroll';

import Intro from "../Home/Home";
import Skill from "../Skills/Skill";
import Resume from "../Resume/Resume";
import Project from "../Project/Project";
import Contact from "../Contact/Contact";
import Main from "./Main";

/**
 * Wrapper component containing the Main component and Scroll component with specified sections.
 */
const Wrapper = () => {
    const [currentSection, setCurrentSection] = useState(0);
    const projectRef = useRef();

    const sections = [
        <Intro />,
        <Skill />,
        <Project ref={projectRef} />,
        <Resume />,
        <Contact />,
    ];

    const handleSectionChange = (index) => {
        setCurrentSection(index);

        // Scroll to the Project component when it's the active section
        if (index === 2 && projectRef.current) {
            projectRef.current.scrollIntoView({ behavior: "smooth" });
        }
    };

    return (
        <>
            <Main>
                <Scroll
                    components={sections}
                    currentSection={currentSection}
                    onSectionChange={handleSectionChange}
                />
            </Main>
        </>
    );
};

export default Wrapper;
