import React, { useRef, useState, useEffect } from "react";
import {
    Container,
    Typography,
    Box,
    useTheme,
    useMediaQuery,
    responsiveFontSizes,
    ThemeProvider,
} from "@mui/material";
import { useTransform, useScroll, AnimatePresence, motion } from "framer-motion";
import Single from "./Single";

const Project = React.forwardRef((props, ref) => {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
    const responsiveTheme = responsiveFontSizes(theme);
    const [currentProjectId, setCurrentProjectId] = useState(projectsData[0].id);

    useEffect(() => {
        // Set the initial project id
        setCurrentProjectId(projectsData[0].id);
    }, []);

    const handleProjectChange = (projectId) => {
        setCurrentProjectId(projectId);
    };

    return (
        <ThemeProvider theme={responsiveTheme}>
            <Container sx={{ marginTop: "12.5vh" }}>
                <Box ref={ref}>
                    <Typography
                        color={"#FFF"}
                        fontWeight={"bold"}
                        variant="h2"
                        mb={3}
                        style={{
                            fontSize: isSmallScreen ? "1.5em" : "3em",
                            textAlign: "center",
                        }}
                    >
                        PROJECTS
                    </Typography>
                    <AnimatePresence exitBeforeEnter>
                        {projectsData.map((project) => (
                            <motion.div
                                key={project.id}
                                initial={{ opacity: 0, x: 100 }}
                                animate={{ opacity: 1, x: 0 }}
                                exit={{ opacity: 0, x: -100 }}
                            >
                                <Single
                                    key={project.id}
                                    project={project}
                                    ref={ref}
                                    onAnimationStart={() =>
                                        handleProjectChange(project.id)
                                    }
                                />
                            </motion.div>
                        ))}
                    </AnimatePresence>
                </Box>
            </Container>
        </ThemeProvider>
    );
});

export default Project;
