import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
    const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });
    let timeoutId;

    const handleMouseMove = (e) => {
        clearTimeout(timeoutId);
        timeoutId = setTimeout(() => {
            const boundingRect = e.target.getBoundingClientRect();
            const x = e.clientX - boundingRect.left;
            const y = e.clientY - boundingRect.top;
            setCirclePosition({ x, y });
        }, 16); // Update every ~60 frames per second (1000ms / 60fps = 16.67ms)
    };

    const handleMouseLeave = () => {
        setCirclePosition({ x: 0, y: 0 });
        clearTimeout(timeoutId);
    };

    return (
        <>
            <div
                style={{ position: 'relative', display: 'inline-block' }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                {/* 이미지를 감싸는 div를 추가하고 그 안에 이미지를 렌더링합니다. */}
                <img
                    alt={skill.name}
                    style={{ maxWidth: "100%", height: "10vh", objectFit: "cover" }}
                    src={skill.image}
                />

                {/* 동그라미를 표시할 div */}
                <div
                    style={{
                        position: 'absolute',
                        top: `${circlePosition.y}px`,
                        left: `${circlePosition.x}px`,
                        width: '20vh',
                        height: '20vh',
                        borderRadius: '50%',
                        backgroundColor: 'white',
                        border: '1px solid #FFF',
                        display: circlePosition.x === 0 && circlePosition.y === 0 ? 'none' : 'block',
                    }}
                >{skill.name}</div>
            </div>
        </>
    );
};

export default SkillCard;
