import React from "react";

const Main = ({ children }) => {
  const scrollToNextSection = () => {
    // 다음 섹션으로 스크롤
    const main = document.getElementById("main");
    if (main) {
      const nextSection = main.scrollTop + window.innerHeight;
      main.scrollTo({
        top: nextSection,
        behavior: "smooth",
      });
    }
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
      onWheel={scrollToNextSection}
    >
      {React.Children.map(children, (child, index) => (
        <div
          key={index}
          id={`section${index}`}
          className="element"
          style={{
            flex: "1",
            minHeight: "100vh",
            display: "flex",
            flexDirection: "row",
          }}
        >
          {child}
        </div>
      ))}
    </main>
  );
};

export default Main;
