import React, { useEffect } from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const scrollToSection = (index) => {
    scroller.scrollTo(`section${index}`, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;

      // 여기서 각 섹션의 시작 위치를 계산하여 원하는 위치에 도달하면 다음 섹션으로 스크롤
      // 예를 들어, 현재 섹션의 30% 이상 스크롤될 때 다음 섹션으로 이동
      const windowHeight = window.innerHeight;
      const totalHeight = windowHeight * React.Children.count(children);
      const threshold = totalHeight * 0.1;

      const currentIndex = Math.floor(currentScrollPos / windowHeight);

      if (currentScrollPos > currentIndex * windowHeight + threshold) {
        scrollToSection(currentIndex + 1);
      } else if (currentScrollPos < currentIndex * windowHeight - threshold) {
        scrollToSection(currentIndex - 1);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [children]);

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element name={`section${index}`} className="element">
          <div
            key={index}
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
