import React from "react";
import { Slide } from "@mui/material";

const Main = ({ children }) => {
  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Slide direction="up" in={true} mountOnEnter unmountOnExit>
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Slide>
      ))}
    </main>
  );
};

export default Main;
