import React from "react";
import {  animated } from "@react-spring/web";

import { Card, CardContent, Typography, useTheme, responsiveFontSizes, ThemeProvider } from "@mui/material";

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const responsiveTheme = responsiveFontSizes(theme);

  const getRandomColor = () => {
    const letters = "0123456789ABCDEF";
    let color = "#";
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  const randomColor = getRandomColor();

  return (
    <ThemeProvider theme={responsiveTheme}>
        <animated.div>
      <Card variant="outlined" sx={{ width: "90%", height: "100%", backgroundColor: randomColor }}>
        <CardContent>
          <Typography
            fontWeight={"bold"}
            sx={{
              marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
              fontSize: { xs: "8px", sm: "8px", md: "16px" },
            }}
          >
            {skill}
          </Typography>
        </CardContent>
      </Card>
      </animated.div>
    </ThemeProvider>
  );
};

export default SkillCard;
