import React, { useState } from "react";
import { Container, Typography, Card, CardContent, Grid, LinearProgress } from "@mui/material";

// Programming Skills
const programmingSkills = [
    { title: "JavaScript" },
    { title: "React.js" },
    { title: "Java" },
    { title: "C" },
    { title: "Python" },
    { title: "TypeScript" },
    { title: "R" },
];

// Markup and Stylesheet Languages
const markupAndStylesheetSkills = [
    { title: "HTML5" },
    { title: "CSS3" },
    { title: "HTML5" },
    { title: "CSS" },
];

// Database and Data Format
const databaseAndDataFormatSkills = [
    { title: "MySQL" },
    { title: "JSON" },
];

// Frameworks
const frameworkSkills = [
    { title: "React-Native" },
    { title: "FastAPI" },
];

// Design Tool
const designToolSkills = [
    { title: "Figma" },
];

// Tools and Practices
const toolsAndPracticesSkills = [
    { title: "Postman" },
    { title: "Git" },
    { title: "Github" },
    { title: "Linux" },
];


// Skill Card Component
const SkillCard = ({ title, level }) => {
    return (
        <Card>
            <CardContent>
                <Typography variant="h5" component="div" mb={2}>
                    {title}
                </Typography>
                <LinearProgress variant="determinate" value={level} />
                <Typography variant="body2" color="textSecondary" mt={1}>
                    Level: {level}%
                </Typography>
            </CardContent>
        </Card>
    );
};

const Skill = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const handleNextSlide = () => {
        if (currentSlide < 2) { // 총 3개의 슬라이드가 있으므로, 0, 1, 2까지만 허용
            setCurrentSlide(currentSlide + 1);
        }
    };

    const handlePrevSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    return (
        <Container
            id="intro"
            sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                height: "100%",
            }}
        >
            <Typography variant="h2" fontWeight={"bold"} align="center" mb={10}>
                Skills
            </Typography>

            {/* Frontend Skills */}
            <Grid container spacing={3} style={{ transform: `translateX(-${currentSlide * 100}%)`, transition: "transform 0.5s" }}>
                {frontendSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>

            {/* Backend Skills */}
            <Grid container spacing={3} style={{ transform: `translateX(-${currentSlide * 100}%)`, transition: "transform 0.5s" }}>
                {backendSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>

            {/* Common Skills */}
            <Grid container spacing={3} style={{ transform: `translateX(-${currentSlide * 100}%)`, transition: "transform 0.5s" }}>
                {commonSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>

            {/* 좌우 화살표 */}
            <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
                <button onClick={handlePrevSlide}>{'<'}</button>
                <button onClick={handleNextSlide}>{'>'}</button>
            </div>
        </Container>
    );
};


export default Skill;
