import React from "react";
import { Container, Typography, Card, CardContent, Grid, LinearProgress } from "@mui/material";

// Frontend Skills
const frontendSkills = [
    { title: "JavaScript", level: 90 },
    { title: "React.js", level: 85 },
    { title: "HTML5", level: 90 },
    { title: "CSS3", level: 85 },
    { title: "HTML5", level: 95 },
    { title: "CSS", level: 100 },
    { title: "Figma", level: 90 },
];

// Backend Skills
const backendSkills = [
    { title: "Java", level: 90 },
    { title: "C", level: 75 },
    { title: "Python", level: 65 },
    { title: "TypeScript", level: 70 },
    { title: "R", level: 80 },
    { title: "MySQL", level: 85 },
    { title: "React-Native", level: 85 }, // Note: React-Native can be used for mobile (frontend) or backend
    { title: "FastAPI", level: 60 },
    { title: "JSON", level: 90 },
    { title: "Node.js", level: 90 },
];

// Common Skills (Tools and Practices)
const commonSkills = [
    { title: "Postman", level: 80 },
    { title: "Git", level: 95 },
    { title: "Linux", level: 70 },
];
// Calculate average level for a category
const calculateAverageLevel = (skills) => {
    const totalLevels = skills.reduce((sum, skill) => sum + skill.level, 0);
    return totalLevels / skills.length;
};

// Render average skill card
const renderAverageSkillCard = (category, averageLevel) => (
    <Grid item xs={12} sm={6} md={4} key={category}>
        <Card elevation={3}>
            <CardContent>
                <Typography variant="h6" color="textSecondary" gutterBottom>
                    {category}
                </Typography>
                <LinearProgress variant="determinate" value={averageLevel} />
                <Typography variant="body2" color="textSecondary" mt={2}>
                    Average Level: {averageLevel}%
                </Typography>
            </CardContent>
        </Card>
    </Grid>
);

// Skill Component
const Skill = () => {
    return (
        <div style={{ overflowY: "auto", maxHeight: "calc(100vh - 64px)" }}>
            <Container id="intro" sx={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", height: "100%" }}>
                <Typography variant="h2" fontWeight={"bold"} align="center" mt={5} mb={3}>
                    Skills
                </Typography>
                <Grid container spacing={3}>
                    {/* Frontend Skills */}
                    {renderAverageSkillCard("Frontend Skills", calculateAverageLevel(frontendSkills))}

                    {/* Backend Skills */}
                    {renderAverageSkillCard("Backend Skills", calculateAverageLevel(backendSkills))}

                    {/* Common Skills */}
                    {renderAverageSkillCard("Common Skills", calculateAverageLevel(commonSkills))}
                </Grid>
            </Container>
        </div>
    );
};


export default Skill;
