import React, { useState } from 'react';
import { Box } from '@mui/system';

const SkillCard = ({ skill }) => {
  const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e) => {
    setCirclePosition({ x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <>
      <Box
        style={{
          display: 'flex',
          justifyContent: 'center',
          position: 'relative',
          marginBottom: '5vh',
        }}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      >
        <div
          className={`skill-icon ${isHovered ? 'flipped' : ''}`}
          style={{
            position: 'relative',
            maxWidth: '150%',
            height: '8vh',
            objectFit: 'cover',
            zIndex: 1,
          }}
        >
          <img alt={skill.name} src={skill.image} />
        </div>
        {circlePosition.x !== 0 && (
          <Box
            style={{
              position: 'absolute',
              left: circlePosition.x,
              top: circlePosition.y,
              transform: `translate(-50%, -50%)`,
            }}
          >
            <Box
              style={{
                width: '5rem',
                height: '5rem',
                backgroundColor: '#66daf9',
                borderRadius: '50%',
                opacity: '0.5',
                zIndex: 0,
              }}
            ></Box>
            <Box
              style={{
                marginTop: '-60%',
                color: '#FFF',
                textAlign: 'center',
                fontWeight: 'bold',
                zIndex: 3,
              }}
            >
              {skill.name}
            </Box>
          </Box>
        )}
      </Box>

      <style>
        {`
          .skill-icon {
            transition: transform 0.5s ease-in-out;
          }

          .skill-icon.flipped {
            transform: rotateY(180deg) perspective(1000px);
          }

          @keyframes moveHorizontal {
            0% {
              transform: rotateY(0deg) perspective(1000px);
            }
            50% {
              transform: rotateY(180deg) perspective(100px);
            }
            100% {
              transform: rotateY(0deg) perspective(1000px);
            }
          }
        `}
      </style>
    </>
  );
};

export default SkillCard;
