import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import { borderRadius, fontWeight } from '@mui/system';

const SkillCard = ({ skill }) => {
    const [hexagonPosition, setHexagonPosition] = useState({ x: 0, y: 0 });

    const handleMouseMove = (e) => {
        const boundingBox = e.currentTarget.getBoundingClientRect();
        const mouseX = e.clientX - boundingBox.left;
        const mouseY = e.clientY - boundingBox.top;

        setHexagonPosition({ x: mouseX, y: mouseY });
    };

    const handleMouseLeave = () => {
        setHexagonPosition({ x: 0, y: 0 });
    };

    return (
        <>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    position: 'relative',
                }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                <img
                    alt={skill.name}
                    style={{ maxWidth: '100%', height: '10vh', objectFit: 'cover' }}
                    src={skill.image}
                />
                {hexagonPosition.x !== 0 && (
                    <svg
                        style={{
                            position: 'absolute',
                            left: hexagonPosition.x,
                            top: hexagonPosition.y,
                            transform: `translate(-50%, -50%)`,
                        }}
                        width="120"
                        height="138"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <polygon
                            points="60,3 114,39 114,99 60,135 6,99 6,39"
                            fill="#FFF"
                            stroke="#000"
                            strokeWidth="2"
                        />
                        <text
                            x="60"
                            y="75"
                            fill="#000"
                            textAnchor="middle"
                            fontWeight="bold"
                        >
                            {skill.name}
                        </text>
                    </svg>
                )}
            </div>
        </>
    );
};

export default SkillCard;
