import React, { useState, useEffect, useRef } from "react";
import { useSpring, animated } from 'react-spring';
import Header from "../Header/Header";

const Scroll = ({ components }) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [scrollPosition, setScrollPosition] = useState(0);

    const scrollRef = useRef();

    const handleScroll = () => {
        const scrollTop = scrollRef.current.scrollTop;
        const componentHeight = scrollRef.current.clientHeight;

        const newIndex = Math.floor(scrollTop / componentHeight);
        setActiveIndex(newIndex);

        // 스크롤 위치 업데이트
        setScrollPosition(scrollTop);
    };

    useEffect(() => {
        const handleResize = () => {
            const componentHeight = scrollRef.current.clientHeight;
            const newIndex = Math.floor(scrollRef.current.scrollTop / componentHeight);
            setActiveIndex(newIndex);
        };

        window.addEventListener("resize", handleResize);

        return () => {
            window.removeEventListener("resize", handleResize);
        };
    }, []);

    const scrollToComponent = (index) => {
        const componentHeight = scrollRef.current.clientHeight;
        scrollRef.current.scrollTo({
            top: index * componentHeight,
            behavior: "smooth",
        });
    };

    const updateSlideState = (index) => {
        scrollToComponent(index);
    };

    const [{ opacity }, set] = useSpring(() => ({ opacity: 1 }));

    useEffect(() => {
        const options = {
            root: null,
            rootMargin: "0px",
            threshold: 0.5, // Adjust threshold as needed
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach((entry) => {
                if (entry.isIntersecting) {
                    const newOpacity = Math.max(0, Math.min(1, (window.innerHeight - entry.boundingClientRect.top) / (entry.boundingClientRect.height)));
                    set({ opacity: newOpacity });
                } else {
                    set({ opacity: 0 });
                }
            });
        }, options);

        // Observer 연결
        observer.observe(scrollRef.current);

        return () => {
            observer.disconnect(); // 컴포넌트가 언마운트될 때 Observer 해제
        };
    }, [set]);

    return (
        <div
            ref={scrollRef}
            onScroll={handleScroll}
            style={{
                height: "100vh",
                overflowY: "scroll",
                scrollBehavior: "smooth",
            }}
        >
            <Header
                orientation={'vertical'}
                length={components.length}
                currIdx={activeIndex}
                clickHandler={updateSlideState}
            />
            {components.map((component, index) => (
                <animated.div
                    key={index}
                    style={{
                        height: "100vh",
                        opacity: opacity.interpolate((o) => index === activeIndex ? o : 0.5),
                        transition: "opacity 0.5s ease-in-out",
                    }}
                >
                    {React.cloneElement(component, { isActive: index === activeIndex, scrollPosition })}
                </animated.div>
            ))}
        </div>
    );
};

export default Scroll;
