import { useRef } from "react";
import { motion, useSpring, useTransform, useMotionValue, useVelocity, useAnimationFrame, useScroll } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100 }) {
    const baseX = useMotionValue(0);
    const { scrollY } = useScroll();
    const scrollVelocity = useVelocity(scrollY);
    const smoothVelocity = useSpring(scrollVelocity, {
        damping: 20,
        stiffness: 200
    });
    const velocityFactor = useTransform(smoothVelocity, [0, 1], [2, 0], {
        clamp: false
    });

    const x = useTransform(baseX, (v) => `${wrap(-50, 50, v)}%`);

    const directionFactor = useRef(1);
    useAnimationFrame((t, delta) => {
        let moveBy = baseVelocity * (delta / 1000);

        if (velocityFactor.get() < 0) {
            directionFactor.current = -1;
        } else if (velocityFactor.get() > 0) {
            directionFactor.current = 1;
        }

        // moveBy += directionFactor.current * moveBy * velocityFactor.get();

        baseX.set(baseX.get() + moveBy);
    });
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <motion.div className="scroller" style={{ x: x, width: '100%' }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h1" mb={3} style={{ fontSize: isSmallScreen ? "1.5em" : "3em", textAlign: "center", }}>
                    {children}
                </Typography>
            </motion.div>
        </div>
    );
}

export default function App() {
    return (
        <section>
            <ParallaxText baseVelocity={-5}>LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S LET'S</ParallaxText>
            <ParallaxText baseVelocity={5}>START START START START START START START START START START START START START START START START START START START START</ParallaxText>
        </section>
    );
}
