import React, { useRef, useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardContent, Typography, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);
  const domTarget = useRef(null);
  const [opacity, setOpacity] = useState(0);

  const handleScroll = () => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.6, // Adjust as needed
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          console.log('Element is in the viewport.');
          const newOpacity = Math.min(1, Math.max(0, (window.innerHeight - entry.boundingClientRect.top) / (entry.boundingClientRect.height)));
          setOpacity(newOpacity);
        } else {
          console.log('Element not in the viewport.');
          // Set opacity to 0 when not in the viewport
          setOpacity(0);
        }
      });
    }, options);

    if (domTarget.current) {
      observer.observe(domTarget.current);
    }
  };

  useEffect(() => {
    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Initial calculation
    handleScroll();

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <ThemeProvider theme={responsiveTheme}>
      <animated.div
        ref={domTarget}
        style={{
          opacity: opacity,
          transition: "opacity 0.5s ease",
        }}
      >
        <Card variant="outlined" sx={{ width: "90%", height: "100%" }}>
          <CardContent>
            <Typography
              fontWeight={"bold"}
              sx={{
                marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
                fontSize: { xs: "8px", sm: "8px", md: "16px" },
              }}
            >
              {skill}
            </Typography>
          </CardContent>
        </Card>
      </animated.div>
    </ThemeProvider>
  );
};

export default SkillCard;
