import React, { useState, useEffect } from "react";
import { Container, Typography, Card, CardContent, Grid, LinearProgress } from "@mui/material";
import useTheme from "@mui/system/useTheme";
// Frontend Skills
const frontendSkills = [
    { title: "JavaScript", level: 90 },
    { title: "React.js", level: 85 },
    { title: "HTML5", level: 90 },
    { title: "CSS3", level: 85 },
    { title: "HTML5", level: 95 },
    { title: "CSS", level: 100 },
    { title: "Figma", level: 90 },
];

// Backend Skills
const backendSkills = [
    { title: "Java", level: 90 },
    { title: "C", level: 75 },
    { title: "Python", level: 65 },
    { title: "TypeScript", level: 70 },
    { title: "R", level: 80 },
    { title: "MySQL", level: 85 },
    { title: "React-Native", level: 85 }, // Note: React-Native can be used for mobile (frontend) or backend
    { title: "FastAPI", level: 60 },
    { title: "JSON", level: 90 },
    { title: "Node.js", level: 90 },
];

// Common Skills (Tools and Practices)
const commonSkills = [
    { title: "Postman", level: 80 },
    { title: "Git", level: 95 },
    { title: "Linux", level: 70 },
];

// Skill Card Component
const SkillCard = ({ title, level }) => {
    return (
        <Card>
            <CardContent>
                <Typography variant="h5" component="div" mb={2}>
                    {title}
                </Typography>
                <LinearProgress variant="determinate" value={level} />
                <Typography variant="body2" color="textSecondary" mt={1}>
                    Level: {level}%
                </Typography>
            </CardContent>
        </Card>
    );
};

// Skill Component
const Skill = () => {
    const theme = useTheme();
    const [isMobile, setIsMobile] = useState(window.innerWidth < theme.breakpoints.values.md);

    const handleResize = () => {
        setIsMobile(window.innerWidth < theme.breakpoints.values.md);
    };

    useEffect(() => {
        window.addEventListener("resize", handleResize);

        return () => {
            window.removeEventListener("resize", handleResize);
        };
    }, []);

    const cardGridSize = isMobile ? 12 : 4; // 조건에 따라 카드 열의 크기 조절

    return (
        <Container
            id="intro"
            sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                height: "100%",
            }}
        >
            <Typography variant="h2" fontWeight={"bold"} align="center" mb={10}>
                Skills
            </Typography>

            {/* Frontend Skills */}
            <Grid container spacing={3}>
                {frontendSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={cardGridSize} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>

            {/* Backend Skills */}
            <Grid container spacing={3}>
                {backendSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={cardGridSize} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>

            {/* Common Skills */}
            <Grid container spacing={3}>
                {commonSkills.map((skill, index) => (
                    <Grid item xs={12} sm={6} md={cardGridSize} key={index}>
                        <SkillCard {...skill} />
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Skill;
