import React, { useState, useEffect, useRef } from "react";
import { motion, useScroll } from "framer-motion";
import {
    Container,
    Typography,
    Box,
    useTheme,
    Button,
} from "@mui/material";

const Single = React.forwardRef(({ project }, ref) => {
    const imageUrl = project.imageUrls[0];

    const imageVariants = {
        hidden: { opacity: 0, scale: 0.8 },
        visible: { opacity: 1, scale: 1, transition: { duration: 0.5 } },
    };

    return (
        <Container>
            <Box style={{ width: '100%' }}>
                <Box ref={ref}>
                    <motion.img
                        alt={project.title}
                        src={imageUrl}
                        style={{
                            minHeight: "0",
                            minWidth: "0",
                            maxHeight: "50vh",
                            maxWidth: "80%",
                            border: "1px solid #65f9af",
                            borderRadius: "10px",
                            // opacity: isHovered ? 0.1 : 1,
                        }}
                        variants={imageVariants}
                        initial="hidden"
                        animate="visible"
                    />
                </Box>

                <motion.div>
                    <Typography
                        color={'#FFF'}
                        fontWeight={"bold"}
                        textAlign={"center"}
                        mb={2}
                        sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
                    >
                        {project.title}
                    </Typography>
                    <Typography
                        color={'#FFF'}
                        fontWeight={"bold"}
                        textAlign={"center"}
                        variant="body2"
                        sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                    >
                        {project.role}
                    </Typography>
                    <Typography
                        color={'#FFF'}
                        variant="body2"
                        textAlign={"center"}
                        mb={2}
                        sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                    >
                        {project.description}
                    </Typography>
                    <Box sx={{ marginTop: "auto", textAlign: "center" }}>
                        {project.demoLink ? (
                            <Button
                                href={project.demoLink}
                                variant="contained"
                                target="_blank"
                                sx={{ fontSize: { xs: "8px", sm: "14px" }, backgroundColor: '#65f9af', color: '#327C57' }}
                            >
                                Demo
                            </Button>
                        ) : (
                            <Typography
                                variant="body2"
                                color={'#FFF'}
                                textAlign={"center"}
                                mb={2}
                                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                            >
                                Demo Not Available
                            </Typography>
                        )}
                    </Box>
                </motion.div>
            </Box>
        </Container>
    );
});

export default Single;
