import React, { useState, useEffect } from "react";
import Header from "../Header";
import Intro from "../Intro";
import Skill from "../Skill";
import Resume from "../Resume";
import Project from "../Project";
import Contact from "../Contact";
import Footer from "../Footer";
import Main from "../Main";

const Home = () => {
  const [currentSection, setCurrentSection] = useState(0);

  const scrollToSection = (index) => {
    const sectionIds = ["intro", "skill", "project", "resume", "contact"];
    const sectionElement = document.getElementById(sectionIds[index]);

    if (sectionElement) {
      sectionElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleScroll = () => {
    const scrollPosition = window.scrollY;
    const sectionOffsets = [0, 0, 0, 0, 0]; // Adjust these values based on your layout

    for (let i = 0; i < sectionOffsets.length; i++) {
      if (scrollPosition >= sectionOffsets[i] && scrollPosition < sectionOffsets[i + 1]) {
        setCurrentSection(i);
        break;
      }
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Header />
      <Main>
        <Intro id="intro" />
        <Skill id="skill" />
        <Project id="project" />
        <Resume id="resume" />
        <Contact id="contact" />
      </Main>
      <Footer />
    </>
  );
};

export default Home;
