import React, { useState, useEffect, useCallback, useRef, useMemo } from 'react';
import { Box } from '@mui/material';
import NavBar from './Slides/NavBar';

const Slides = (props) => {
  const { components } = props;
  const transitionSpeed = 150;

  const [idxState, setIdxState] = useState({
    currVIdx: 0,
    pastVIdx: 0,
    currHIdx: [],
    pastHIdx: [],
    isSwitching: false,
  });

  const containerRef = useRef(null);

  const isHorizontal = useCallback((slide) => slide?.length !== undefined, []);

  useEffect(() => {
    setIdxState((prevState) => ({
      ...prevState,
      currHIdx: components.map((slide) => (isHorizontal(slide) ? 0 : -1)),
      pastHIdx: components.map((slide) => (isHorizontal(slide) ? 0 : -1)),
    }));
  }, [components, isHorizontal]);

  const vIdxSetter = useCallback(
    (newIdx) => {
      setIdxState((prevState) => ({
        ...prevState,
        pastHIdx: [...prevState.currHIdx],
        pastVIdx: prevState.currVIdx,
        isSwitching: true,
      }));

      window.setTimeout(() => {
        setIdxState((prevState) => ({
          ...prevState,
          isSwitching: false,
          currVIdx: newIdx,
        }));
      }, transitionSpeed);
    },
    [transitionSpeed]
  );

  const hIdxSetter = useCallback(
    (newIdx) => {
      setIdxState((prevState) => ({
        ...prevState,
        pastVIdx: prevState.currVIdx,
        pastHIdx: [...prevState.pastHIdx],
        pastHIdx: prevState.currHIdx[prevState.currVIdx],
        isSwitching: true,
      }));

      window.setTimeout(() => {
        setIdxState((prevState) => ({
          ...prevState,
          isSwitching: false,
          currHIdx: [...prevState.currHIdx],
          currHIdx: prevState.currHIdx[prevState.currVIdx] = newIdx,
        }));
      }, transitionSpeed);
    },
    [transitionSpeed]
  );

  const scrollTimeout = useRef(null);
  const acceptScroll = useRef(true);
  const deltaY = useRef(0);
  const deltaX = useRef(0);
  const prevX = useRef(-1);
  const prevY = useRef(-1);

  const scrollReset = () => {
    scrollTimeout.current = undefined;
    acceptScroll.current = true;
    deltaY.current = 0;
    deltaX.current = 0;
    prevY.current = -1;
    prevX.current = -1;
  };

  const scrollTimeoutEvent = () => {
    if (Math.abs(deltaY.current) >= Math.abs(deltaX.current)) {
      if (deltaY.current > 50 && idxState.currVIdx < components.length - 1) {
        vIdxSetter(idxState.currVIdx + 1);
      } else if (deltaY.current < -50 && idxState.currVIdx > 0) {
        vIdxSetter(idxState.currVIdx - 1);
      }
    } else {
      if (idxState.currHIdx[idxState.currVIdx] !== -1) {
        if (
          deltaX.current > 50 &&
          idxState.currHIdx[idxState.currVIdx] <
            (components[idxState.currVIdx] || []).length - 1
        ) {
          hIdxSetter(idxState.currHIdx[idxState.currVIdx] + 1);
        } else if (deltaX.current < -50 && idxState.currHIdx[idxState.currVIdx] > 0) {
          hIdxSetter(idxState.currHIdx[idxState.currVIdx] - 1);
        }
      }
    }

    window.setTimeout(scrollReset, 30);
  };

  const scrollEventHandler = useCallback((e) => {
    if (acceptScroll.current) {
      if (scrollTimeout.current) {
        clearTimeout(scrollTimeout.current);
      }
      scrollTimeout.current = window.setTimeout(scrollTimeoutEvent, 30);

      if (e.type === 'wheel') {
        deltaY.current += e.deltaY;
        deltaX.current += e.deltaX;
      } else {
        const y = e.targetTouches[0].clientY;
        const x = e.targetTouches[0].clientX;

        if (prevY.current !== -1 || prevX.current !== -1) {
          deltaY.current += prevY.current - y;
          deltaX.current += prevX.current - x;
        }

        prevY.current = y;
        prevX.current = x;
      }
    }
  }, [acceptScroll, scrollTimeoutEvent]);

  const slideStyle = useMemo(
    () => ({
      width: '100%',
      height: '100%',
      position: 'fixed',
      zIndex: 3,
      transition: `opacity ${transitionSpeed}ms linear 0ms`,
      opacity: idxState.isSwitching ? 1 : 0,
    }),
    [idxState.isSwitching, transitionSpeed]
  );

  return (
    <Box
      ref={containerRef}
      sx={{ overscrollBehavior: 'none' }}
      onWheel={scrollEventHandler}
      onTouchMove={scrollEventHandler}
    >
      <NavBar
        orientation="vertical"
        length={components.length}
        currIdx={idxState.currVIdx}
        clickHandler={vIdxSetter}
      />
      {isHorizontal(components[idxState.currVIdx]) ? (
        <NavBar
          orientation="horizontal"
          length={(components[idxState.currVIdx] || []).length}
          currIdx={idxState.currHIdx[idxState.currVIdx]}
          clickHandler={hIdxSetter}
        />
      ) : undefined}
      <Box
        sx={{
          ...slideStyle,
          visibility: idxState.isSwitching ? undefined : 'hidden',
        }}
      >
        {isHorizontal(components[idxState.pastVIdx])
          ? (components[idxState.pastVIdx] || [])[
              idxState.pastHIdx[idxState.pastVIdx]
            ]
          : components[idxState.pastVIdx]}
      </Box>
      <Box sx={{ ...slideStyle, zIndex: 2 }}>
        {isHorizontal(components[idxState.currVIdx])
          ? (components[idxState.currVIdx] || [])[
              idxState.currHIdx[idxState.currVIdx]
            ]
          : components[idxState.currVIdx]}
      </Box>
    </Box>
  );
};

export default Slides;
