import React, { useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/Home/home';
import Resume from './components/Resume/resume';
import Project from './components/Project/project';
import Skills from './components/Skills/skills';
import Contact from './components/Contact/contact';

const App = () => {
  const homeRef = useRef(null);
  const resumeRef = useRef(null);
  const projectsRef = useRef(null);
  const skillsRef = useRef(null);
  const contactRef = useRef(null);

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.5, // Adjust this threshold as needed
    };

    const callback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          // Element is in the viewport
          const sectionId = entry.target.id;
          console.log(`Section ${sectionId} is now visible`);
          // You can perform actions when each section becomes visible
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe each section
    if (homeRef.current) observer.observe(homeRef.current);
    if (resumeRef.current) observer.observe(resumeRef.current);
    if (projectsRef.current) observer.observe(projectsRef.current);
    if (skillsRef.current) observer.observe(skillsRef.current);
    if (contactRef.current) observer.observe(contactRef.current);

    return () => {
      // Cleanup: Disconnect the observer when the component unmounts
      observer.disconnect();
    };
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} ref={homeRef} />
        <Route path="/resume" element={<Resume />} ref={resumeRef} />
        <Route path="/projects" element={<Project />} ref={projectsRef} />
        <Route path="/skills" element={<Skills />} ref={skillsRef} />
        <Route path="/contact" element={<Contact />} ref={contactRef} />
        {/* Add more routes as needed */}
      </Routes>
    </Router>
  );
}

export default App;
