import React, { useState, useEffect } from "react";
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";

const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const Skill = () => {
  const [isMouseOver, setIsMouseOver] = useState(false);

  useEffect(() => {
    const handleMouseOver = () => {
      setIsMouseOver(true);
    };

    const handleMouseOut = () => {
      setIsMouseOver(false);
    };

    document.addEventListener("mouseover", handleMouseOver);
    document.addEventListener("mouseout", handleMouseOut);

    return () => {
      document.removeEventListener("mouseover", handleMouseOver);
      document.removeEventListener("mouseout", handleMouseOut);
    };
  }, []);

  return (
    <Container
      id="intro"
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100%",
      }}
    >
      <Typography variant="h4" fontWeight={"bold"} align="center" mb={5}>
        Skills
      </Typography>

      <Grid container spacing={1}>
        {skillsData.map((category, index) => (
          <Grid item key={index} xs={12} sm={6} md={4}>
            <Typography variant="h6" color={"GrayText"} mb={2} textAlign={"center"}>
              {category.category}
            </Typography>
            <Grid container spacing={1}>
              {category.skills.map((skill, skillIndex) => (
                <Grid item key={skillIndex} xs={5} sm={4} md={6}>
                  <Card
                    variant="outlined"
                    sx={{
                      transition: "transform 0.3s ease",
                      transform: isMouseOver ? "translateY(-10px)" : "translateY(0)",
                    }}
                  >
                    <CardContent>
                      <Typography variant="body1" fontWeight={"bold"} textAlign={"center"}>
                        {skill}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Skill;
